import React, { Component } from 'react'
import { Card, CardBody, CardHeader } from 'reactstrap'
import axios from 'axios'
import Draggable from 'react-draggable'

export default class FloatWindow extends Component {
    state = {
        top: 60,
        left: 15,
        types: {
            'Jenis Kelamin': 'jenis_kelamin',
            'Agama': 'agama',
            'Pendidikan': 'pendidikan',
            'Golongan Darah': 'golongan_darah',
            'Pekerjaan': 'pekerjaan',
        },
        type: 'Jenis Kelamin'
    }

    onchange = ev => {
        this.setState({ type : ev.target.value });
    }

    render() {
        const { types, type } = this.state;
        return (
            <div style={{ position: 'fixed', maxWidth: 350, width: 350 }}>
                <Draggable
                    defaultPosition={{ x: 227, y: 41 }}
                >
                    <Card style={{
                        maxWidth: 350,
                        background: 'rgba(0,0,0,0.5)',
                        backdropFilter: 'blur(10px)',
                        color: 'white'
                    }} className="border-0 shadow">
                        <CardBody onDrag={e => console.log(e)}>
                            <select className="form-control form-control-sm" onChange={this.onchange} value={type} id="">
                                {Object.keys(types).map((t, i) => (
                                    <option key={i} value={t}>{t}</option>
                                ))}
                            </select>
                            <p className="m-0">Test</p>
                        </CardBody>
                    </Card>
                </Draggable>
            </div>
        )
    }
}
